package nzcovidpass

import (
	"reflect"
	"testing"
	"time"
)

func Test_unprefix(t *testing.T) {
	type args struct {
		prefixObject string
	}
	tests := []struct {
		name    string
		args    args
		want    string
		wantErr bool
	}{
		{
			name: "valid",
			args: args{
				prefixObject: "NZCP:",
			},
			want:    "",
			wantErr: false,
		},
		{
			name: "invalid",
			args: args{
				prefixObject: "HC1:/NOTREALLYQRDATA",
			},
			want:    "",
			wantErr: true,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := unprefix(tt.args.prefixObject)
			if (err != nil) != tt.wantErr {
				t.Errorf("unprefix() error = %v, wantErr %v", err, tt.wantErr)
				return
			}
			if got != tt.want {
				t.Errorf("unprefix() = %v, want %v", got, tt.want)
			}
		})
	}
}

func TestDecode(t *testing.T) {
	type args struct {
		qrdata string
	}
	tests := []struct {
		name    string
		args    args
		want    *verifiedCredential
		wantErr bool
	}{
		{
			name: "tampered payload",
			args: args{
				qrdata: "NZCP:",
			},
			want:    nil,
			wantErr: true,
		},
		{
			name: "valid",
			args: args{
				qrdata: "NZCP:",
			},
			want: &verifiedCredential{
				Ctx: []string{
					"https://www.w3.org/2018/credentials/v1",
					"https://nzcp.covid19.health.nz/contexts/v1",
				},
				Version: "1.0.0",
				Type: []string{
					"VerifiableCredential",
					"PublicCovidPass",
				},
				Csubject: credentialSubject{
					GivenName:  "Jack",
					FamilyName: "Sparrow",
					Dob:        "1960-04-16",
				},
			},
			wantErr: false,
		},
		// TODO: Add test cases.
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := Decode(tt.args.qrdata)
			if (err != nil) != tt.wantErr {
				t.Errorf("Decode() error = %v, wantErr %v", err, tt.wantErr)
				return
			}
			if got != nil {
				if !reflect.DeepEqual(got.u.claims.Vc, *tt.want) {
					t.Errorf("Decode() = %v, want %v", got, tt.want)
				}
			}
		})
	}
}

// Every test case available at https://nzcp.covid19.health.nz/#examples
func TestDecodeAndVerify(t *testing.T) {
	type args struct {
		qrdata string
	}
	tests := []struct {
		name    string
		args    args
		want    *Decoded
		wantErr bool
	}{
		{
			name: "valid",
			args: args{
				qrdata: "NZCP:",
			},
			want: &Decoded{
				Expiration: time.Unix(1951416330, 0),
				NotBefore:  time.Unix(1635883530, 0),

				Vc: verifiedCredential{
					Ctx: []string{
						"https://www.w3.org/2018/credentials/v1",
						"https://nzcp.covid19.health.nz/contexts/v1",
					},
					Version: "1.0.0",
					Type: []string{
						"VerifiableCredential",
						"PublicCovidPass",
					},
					Csubject: credentialSubject{
						GivenName:  "Jack",
						FamilyName: "Sparrow",
						Dob:        "1960-04-16",
					},
				},
			},
			wantErr: false,
		},
		{
			name: "bad public key",
			args: args{
				qrdata: "NZCP:",
			},
			want:    &Decoded{},
			wantErr: true,
		},
		{
			name: "no public key",
			args: args{
				qrdata: "NZCP:",
			},
			want:    &Decoded{},
			wantErr: true,
		},
		{
			name: "modified signature",
			args: args{
				qrdata: "NZCP:",
			},
			want:    &Decoded{},
			wantErr: true,
		},
		{
			name: "modified payload",
			args: args{
				qrdata: "NZCP:",
			},
			want:    &Decoded{},
			wantErr: true,
		},
		{
			name: "expired pass",
			args: args{
				qrdata: "NZCP:",
			},
			want:    &Decoded{},
			wantErr: true,
		},
		{
			name: "inactive pass",
			args: args{
				qrdata: "NZCP:",
			},
			want:    &Decoded{},
			wantErr: true,
		},
	}
	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			certprov, _ := NewNZCertificateProvider()
			got, err := DecodeAndVerify(tt.args.qrdata, certprov)
			if (err != nil) != tt.wantErr {
				t.Errorf("DecodeAndVerify() error = %v, wantErr %v", err, tt.wantErr)
				return
			}
			if got != nil {
				if !reflect.DeepEqual(got.Vc, tt.want.Vc) || got.Expiration != tt.want.Expiration || got.NotBefore != tt.want.NotBefore {
					t.Errorf("Decode() = %v, want %v", got, tt.want)
				}
			}
		})
	}
}
